svg.script:

    function buttonEvent(event) {

      if ((event.type == "click" && event.button == 0) ||
          (event.type == "keydown" &&
	  (event.keyCode == 32 || event.keyCode ==13))) {


    var SVGDocument = event.target.ownerDocument;


    var ButtonPost  = SVGDocument.getElementById("PostField");
    var ButtonPost_Text  = SVGDocument.getElementById("PostField_Text");
    var fill = "none";  



        if(event.target.id == "ButtonSEND" || event.target.id == "ButtonSEND_Text") 
        {
            fill = "black";

            var text = "Individual Text enter here..";
            ButtonPost.style.setProperty("fill", fill, "");
        }

        else if (ButtonPost.style.fill == "black" && 
        (event.target.id == "ButtonDELETE" || event.target.id == "ButtonDELETE_Text")) 
        {

            fill = "none";

            ButtonPost.style.setProperty("fill", fill, "");
            ButtonPost_Text.textContent="";
        }









      }
    }

